CREATE OR REPLACE FORCE VIEW apex_030200.wwv_flow_activity_log (time_stamp,component_type,component_name,component_attribute,information,elap,num_rows,userid,ip_address,user_agent,flow_id,step_id,session_id,security_group_id,sqlerrm,sqlerrm_component_type,sqlerrm_component_name,page_mode,cached_regions,application_info) AS
select "TIME_STAMP","COMPONENT_TYPE","COMPONENT_NAME","COMPONENT_ATTRIBUTE","INFORMATION","ELAP","NUM_ROWS","USERID","IP_ADDRESS","USER_AGENT","FLOW_ID","STEP_ID","SESSION_ID","SECURITY_GROUP_ID","SQLERRM","SQLERRM_COMPONENT_TYPE","SQLERRM_COMPONENT_NAME","PAGE_MODE","CACHED_REGIONS","APPLICATION_INFO" from wwv_flow_activity_log1$ union all
    select "TIME_STAMP","COMPONENT_TYPE","COMPONENT_NAME","COMPONENT_ATTRIBUTE","INFORMATION","ELAP","NUM_ROWS","USERID","IP_ADDRESS","USER_AGENT","FLOW_ID","STEP_ID","SESSION_ID","SECURITY_GROUP_ID","SQLERRM","SQLERRM_COMPONENT_TYPE","SQLERRM_COMPONENT_NAME","PAGE_MODE","CACHED_REGIONS","APPLICATION_INFO" from wwv_flow_activity_log2$;